var dice = require('./index');
var server = require('http').createServer(function (req, res) {
	res.writeHead(200, {'Content-Type': 'text/plain'});
	dice.roll('1d20', 'olli', 'game1');
	dice.roll('1d1', 'olli', 'game1');

	dice.on('rolling', function (die, player, game) {
		res.write(player + ' rolling ' + die);
	});
	dice.on('roll', function (roll, player, game) {
		res.write(player + ' rolled ' + roll);
	});
	dice.on('result', function (dice, rolls, result, player, game) {
		res.write(player + ' got ' + result);
	});
	dice.on('misroll', function (error, roller, game) {
		res.end(error.message);
	});
}).listen(9090);
